import React from "react";
import { Button, Form, FormProps, Image, Input, Select, Switch } from "antd";
import Dragger from "antd/es/upload/Dragger";
import { InboxOutlined } from "@ant-design/icons";

export type FieldType = {
  title?: string;
  description?: string;
  websiteUrl?: string;
  repoUrl?: string;
  stack?: string[];
  isSimple?: boolean;
};

interface ItemsFormProps {
  form?: any;
  onFinish: FormProps["onFinish"];
  loading?: boolean;
  handleFile: (file: File | null) => void;
  imagePreview?: string;
}

export const ItemsForm: React.FC<ItemsFormProps> = ({
  form,
  onFinish,
  loading = false,
  handleFile,
  imagePreview,
}) => {
  // state

  return (
    <Form
      layout="vertical"
      style={{ maxWidth: 600, margin: "0 auto" }}
      onFinish={onFinish}
      disabled={loading}
      form={form}
    >
      <Form.Item<FieldType>
        label="Title"
        name="title"
        rules={[{ required: true, message: "Title is required" }]}
      >
        <Input />
      </Form.Item>

      <Form.Item<FieldType>
        label="Description"
        name="description"
        rules={[{ required: true, message: "Description is required" }]}
      >
        <Input.TextArea />
      </Form.Item>

      <Form.Item<FieldType> label="Website URL" name="websiteUrl">
        <Input />
      </Form.Item>

      <Form.Item<FieldType>
        label="Repo URL"
        name="repoUrl"
        rules={[{ required: true, message: "Repo URL is required" }]}
      >
        <Input />
      </Form.Item>

      <Form.Item<FieldType>
        label="Stack"
        name="stack"
        rules={[{ required: true, message: "Stack is required" }]}
      >
        <Select mode="tags" />
      </Form.Item>

      <Form.Item<FieldType> label="Not a React App" name="isSimple">
        <Switch />
      </Form.Item>

      {imagePreview && (
        <div
          style={{
            marginBottom: "20px",
            display: "flex",
            flexDirection: "column",
            gap: "10px",
          }}
        >
          <span>Image preview</span>
          <Image
            width={300}
            height={300}
            src={imagePreview}
            fallback="data:image/png;base64,"
          />
        </div>
      )}

      <Form.Item label="Upload Image">
        <Dragger
          onRemove={() => handleFile(null)}
          maxCount={1}
          customRequest={(options: any) => {
            handleFile(options.file);
            options.onSuccess(options.file);
          }}
        >
          <p className="ant-upload-drag-icon">
            <InboxOutlined />
          </p>
          <p className="ant-upload-text">
            Click or drag file to this area to upload image
          </p>
          <p className="ant-upload-hint">Support for a single upload.</p>
        </Dragger>
      </Form.Item>

      <Form.Item style={{ display: "flex", justifyContent: "center" }}>
        <Button type="primary" htmlType="submit" size="large">
          Submit
        </Button>
      </Form.Item>
    </Form>
  );
};
